import React from 'react';
import { connect } from 'react-redux';
import { fetchStream, editStream } from '../../actions';
import StreamForm from './StreamForm';

//we use class-based comp so we could use ComponentDidMount() method
class StreamEdit extends React.Component {

    componentDidMount() {
        this.props.fetchStream(this.props.match.params.id);
    }

    onSubmit = (formValues) => {
        console.log(formValues);
    }

    render() {
        if (!this.props.stream) {
            return <div>Loading...</div>;
        }
        return <div>{this.props.stream.title}</div>
    }
};

//ownProps is a reference to the props obj that shows inside StreamEdit comp
//we use it bc some info we have in props obj inside StreamEdit & some info in 
//the state obj
const mapStateToProps = (state, ownProps) => {
    return { stream:  state.streams[ownProps.match.params.id] };
}

export default connect(mapStateToProps, { fetchStream })(StreamEdit);
