import React from 'react';
import { connect } from 'react-redux';
import { fetchStream } from '../../actions';

//we use class-based comp so we could use ComponentDidMount() method
class StreamEdit extends React.Component {

    componentDidMount() {
        this.props.fetchStream(this.props.match.params.id);
    }


    render() {
        return(
            <div>
                StreamEdit
            </div>
        );
    }
};

//ownProps is a reference to the props obj that shows inside StreamEdit comp
//we use it bc some info we have in props obj inside StreamEdit & some info in 
//the state obj
const mapStateToProps = (state, ownProps) => {
    return { stream:  state.streams[ownProps.match.params.id] };
}

export default connect(mapStateToProps)(StreamEdit);
